INSERT INTO `posts` (`id`, `author_id`, `title`, `description`, `status`, `views`, `create_at`, `update_at`, `slug`, `type`) VALUES
(36, 7, 'Hành tây có tác dụng chống ung thư buồng trứng phát triển', '<p>Theo Tổ chức Y tế Thế giới, ung thư buồng trứng biểu m&ocirc; l&agrave; ung thư buồng trứng phổ biến nhất hiện nay v&agrave; tỷ lệ sống s&oacute;t l&agrave; khoảng 40% trong v&ograve;ng 5 năm. C&ograve;n phần lớn c&aacute;c bệnh nh&acirc;n (80%) sẽ bị t&aacute;i ph&aacute;t sau khi h&oacute;a trị. Đ&oacute; l&agrave; l&yacute; do c&aacute;c nh&agrave; khoa học đang t&igrave;m kiếm một h&igrave;nh thức điều trị hiệu quả hơn.</p>\r\n\r\n<p>Mới đ&acirc;y, c&aacute;c chuy&ecirc;n gia nghi&ecirc;n cứu của Đại học Kamumoto Nhật Bản đ&atilde; ph&aacute;t hiện ra một hợp chất c&oacute; trong h&agrave;nh t&acirc;y, c&oacute; t&ecirc;n l&agrave; Onionine A, theo họ, n&oacute; sở hữu c&aacute;c hoạt t&iacute;nh chống ung thư buồng trứng.</p>\r\n\r\n<p>Nghi&ecirc;n cứu được c&ocirc;ng bố trong c&aacute;c b&aacute;o c&aacute;o khoa học cho thấy hợp chất n&agrave;y l&agrave;m giảm sự tiến triển của c&aacute;c khối u buồng trứng ung thư &aacute;c t&iacute;nh bằng c&aacute;ch can thiệp với chức năng chuy&ecirc;n gia khối u của c&aacute;c tế b&agrave;o d&ograve;ng tủy. Mặt kh&aacute;c, c&aacute;c chuy&ecirc;n gia cũng khẳng định, Onionine A c&oacute; thể cải thiện hiệu quả của thuốc chống ung thư bằng c&aacute;ch tăng khả năng chống tăng sinh c&aacute;c khối u.</p>\r\n\r\n<p>Cũng trong một th&iacute; nghiệm tiến h&agrave;nh tr&ecirc;n chuột, c&aacute;c chuy&ecirc;n gia đ&atilde; cho thấy khối u buồng trứng suy giảm v&agrave; tuổi thọ của chuột tăng. Họ cũng đưa ra kết luận Onionine A c&oacute; trong h&agrave;nh t&acirc;y c&oacute; thể thay thế cho điều trị bổ sung đối với c&aacute;c bệnh nh&acirc;n ung thư buồng trứng. Điều n&agrave;y đ&atilde; thực sự mở ra cơ hội điều trị cho bệnh nh&acirc;n ung thư buồng trứng.</p>\r\n', '5', 0, '2017-04-12 15:58:04', '0000-00-00 00:00:00', 'hanh-tay-co-tac-dung-chong-ung-thu-buong-trung-phat-trien', 'post'),
(37, 7, 'Nghiên cứu vaccin nhỏ mũi phòng bệnh do Chlamydia', '<p>Nh&oacute;m nghi&ecirc;n cứu đ&atilde; thử nghiệm tr&ecirc;n chuột cho thấy, vaccin chứa kh&aacute;ng nguy&ecirc;n BD584 đ&atilde; bảo vệ c&aacute;c động vật gặm nhấm khỏi Chlamydia cũng như l&agrave;m giảm x&aacute;c suất h&igrave;nh th&agrave;nh tai biến. Nguy cơ xuất hiện c&aacute;c biến chứng như dịch v&ograve;i trứng, rối loạn dẫn truyền của v&ograve;i trứng, cản trở thụ tinh giảm 87,5% .</p>\r\n\r\n<p>Trong thời gian tới, c&aacute;c nh&agrave; khoa học tập trung kiểm nghiệm vaccin tr&ecirc;n c&aacute;c chủng Chlamydia kh&aacute;c nhau cũng như thử nghiệm với th&agrave;nh phần của vaccin để tạo ra loại vaccin an to&agrave;n v&agrave; hiệu quả hơn.</p>\r\n\r\n<p>Chlamydia l&agrave; bệnh l&acirc;y truyền qua đường t&igrave;nh dục (STD) phổ biến c&oacute; thể l&acirc;y nhiễm cho cả nam v&agrave; nữ. Bệnh c&oacute; thể g&acirc;y ra tổn thương vĩnh viễn, nghi&ecirc;m trọng đến hệ sinh sản của phụ nữ, l&agrave;m cho phụ nữ kh&oacute; hoặc kh&ocirc;ng thể mang thai về sau. Chlamydia cũng c&oacute; thể g&acirc;y ra hiện tượng thai ngo&agrave;i tử cung (mang thai b&ecirc;n ngo&agrave;i dạ con) c&oacute; thể g&acirc;y tử vong tiềm ẩn.</p>\r\n\r\n<p>Nếu nghi&ecirc;n cứu n&agrave;y th&agrave;nh c&ocirc;ng sẽ l&agrave; phương ph&aacute;p điều trị hữu hiệu, nhẹ nh&agrave;ng cho những người mắc bệnh Chlamydia. Bởi tiến h&agrave;nh ti&ecirc;m chủng vaccin bằng đường nhỏ mũi kh&ocirc;ng g&acirc;y tổn thương cho bệnh nh&acirc;n, ngo&agrave;i ra kh&ocirc;ng cần đến chuy&ecirc;n khoa s&acirc;u v&agrave; đ&agrave;o tạo chuy&ecirc;n ng&agrave;nh y tế.</p>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<p><img src="http://curminlead.ecpvn.com/frontend/web/uploads/images/2017/04/hr3YT29-1492012746.jpg" /></p>\r\n', '5', 0, '2017-04-12 15:59:30', '0000-00-00 00:00:00', 'nghien-cuu-vaccin-nho-mui-phong-benh-do-chlamydia', 'post'),
(38, 7, 'Mỹ thử thành công vắc xin phòng chống virus Zika trên động vật', '<table border="0" cellpadding="0" cellspacing="0" style="background-attachment:initial; background-clip:initial; background-image:initial; background-origin:initial; background-position:initial; background-repeat:initial; background-size:initial; color:rgb(0, 0, 0); font-family:arial; font-size:12px; padding:10px; width:100%">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan="2">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="2">\r\n			<p style="text-align:justify"><span style="color:rgb(0, 51, 102)"><span style="font-size:10pt"><span style="font-family:arial"><strong>YHTH - Ng&agrave;y 28/6, c&aacute;c nh&agrave; khoa học Mỹ c&ocirc;ng bố đ&atilde; thử nghiệm th&agrave;nh c&ocirc;ng vắc xin ph&ograve;ng chống virus Zika tr&ecirc;n động vật trong ph&ograve;ng th&iacute; nghiệm, mở ra hy vọng sớm sản xuất vắc xin d&agrave;nh cho người nhằm ngăn ngừa loại virus đang l&acirc;y lan nhanh ch&oacute;ng n&agrave;y.</strong></span></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="2">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="2">\r\n			<p style="text-align:justify"><span style="font-size:10pt"><span style="font-family:arial"><strong><span style="color:rgb(17, 17, 17)">C&aacute;c nh&agrave; khoa học Mỹ ng&agrave;y 28/6 c&ocirc;ng bố đ&atilde; thử nghiệm th&agrave;nh c&ocirc;ng vắc xin ph&ograve;ng chống virus Zika tr&ecirc;n động vật trong ph&ograve;ng th&iacute; nghiệm, mở ra hy vọng sớm sản xuất vắc xin d&agrave;nh cho người nhằm ngăn ngừa loại virus đang l&acirc;y lan nhanh ch&oacute;ng n&agrave;y.</span></strong></span></span></p>\r\n\r\n			<p style="text-align:justify"><span style="font-size:10pt"><span style="font-family:arial"><span style="color:rgb(17, 17, 17)">Gi&aacute;m đốc Trung t&acirc;m Si&ecirc;u vi khuẩn học v&agrave; Nghi&ecirc;n cứu vắc xin thuộc Trường Y Harvard, &ocirc;ng Dan Barouch cho biết 2 mẫu vắc xin được thử nghiệm tr&ecirc;n chuột trong ph&ograve;ng th&iacute; nghiệm đ&atilde; gi&uacute;p&nbsp;<em>&quot;ngăn chặn ho&agrave;n to&agrave;n&quot;</em>&nbsp;virus Zika, kết quả lạc quan n&agrave;y cho thấy c&oacute; thể ph&aacute;t triển th&agrave;nh c&ocirc;ng loại vắc xin hiệu quả v&agrave; an to&agrave;n chống lại virus Zika d&agrave;nh cho người. &Ocirc;ng Dan Barouch l&agrave; đồng t&aacute;c giả của c&ocirc;ng tr&igrave;nh&nbsp;<strong>thử nghiệm vắc xin ph&ograve;ng chống virus Zika</strong>&nbsp;được c&ocirc;ng bố tr&ecirc;n Tạp ch&iacute; Nature. Đ&aacute;ng ch&uacute; &yacute;, 2 mẫu vắc xin n&agrave;y được thử nghiệm với một liều duy nhất.</span></span></span></p>\r\n\r\n			<p style="text-align:justify"><span style="font-size:10pt"><span style="font-family:arial"><span style="color:rgb(17, 17, 17)">Virus Zika (ZIKV) l&agrave; một virus RNA (arbovirus) thuộc chi Flavivirus, họ Flaviviridae, l&acirc;y truyền chủ yếu qua vết cắn của muỗi Aedes bị nhiễm. T&ecirc;n của virus lấy t&ecirc;n từ khu rừng Zika, gần hồ&nbsp;</span></span></span><span style="color:rgb(17, 17, 17); font-family:arial"><span style="font-size:10pt">Victoria</span><span style="font-size:10pt">&nbsp;ở&nbsp;</span><span style="font-size:10pt">Uganda</span><span style="font-size:10pt">, nơi đ&atilde; t&igrave;m thấy đầu ti&ecirc;n v&agrave;o năm 1947.</span></span></p>\r\n\r\n			<p style="text-align:justify"><span style="font-size:10pt"><span style="font-family:arial"><span style="color:rgb(17, 17, 17)">&nbsp;Theo tổ chức National Geography, &nbsp;năm 2015 dịch bệnh do virus Zika đ&atilde; ph&aacute;t triển ở Trung Mỹ, v&ugrave;ng&nbsp;</span></span></span><span style="color:rgb(17, 17, 17); font-family:arial"><span style="font-size:10pt">Caribbean</span><span style="font-size:10pt">, Nam Mỹ l&agrave; nơi bệnh do virus Zika đ&atilde; đạt đến cấp đại dịch. C&aacute;c trường hợp đầu ti&ecirc;n được ph&aacute;t hiện ở&nbsp;</span><span style="font-size:10pt">Brazil</span><span style="font-size:10pt">; quốc gia n&agrave;y đang bị ảnh hưởng nặng nhất với hơn 1.500.000 trường hợp (0,72% d&acirc;n số&nbsp;</span><span style="font-size:10pt">Brazil</span><span style="font-size:10pt">).</span></span></p>\r\n\r\n			<p style="text-align:justify"><span style="font-size:10pt"><span style="font-family:arial"><span style="color:rgb(17, 17, 17)">&nbsp;Ng&agrave;y 1/ 2/ 2016, Tổ chức Y tế Thế giới (WHO) ban bố t&igrave;nh trạng khẩn cấp y tế to&agrave;n cầu về virus Zika, sau khi tham khảo &yacute; kiến của c&aacute;c chuy&ecirc;n gia về dịch tễ học, y tế c&ocirc;ng cộng, bệnh truyền nhiễm từ khắp thế giới tại một cuộc họp của Ủy ban Khẩn cấp Y tế Quốc tế thuộc WHO.</span></span></span></p>\r\n\r\n			<p style="text-align:justify"><span style="font-size:10pt"><span style="font-family:arial"><span style="color:rgb(17, 17, 17)">&nbsp;Ng&agrave;y 5/4/2016, WHO ra th&ocirc;ng b&aacute;o, virus Zika đ&atilde; l&acirc;y lan tới 61 quốc gia v&agrave; v&ugrave;ng l&atilde;nh thổ tr&ecirc;n thế giới với 130 nước c&oacute; lo&agrave;i muỗi Aedes mang loại virus n&agrave;y, khiến nguy cơ virus Zika lan rộng. Tổ chức n&agrave;y cảnh b&aacute;o dịch Zika c&oacute; thể diễn biến tồi tệ hơn trước khi được kiểm so&aacute;t v&agrave; khuyến c&aacute;o người d&acirc;n thực hiện c&aacute;c biện ph&aacute;p chống muỗi v&agrave; tr&aacute;nh bị muỗi đốt.</span></span></span></p>\r\n\r\n			<p style="text-align:justify"><span style="font-size:10pt"><span style="font-family:arial"><span style="color:rgb(17, 17, 17)">&nbsp;Đ&acirc;y l&agrave; loại virus nguy hiểm, nhất l&agrave; với phụ nữ mang thai v&igrave; c&oacute; khả năng g&acirc;y ra dị tật đầu nhỏ ở thai nhi, cũng như g&acirc;y ra nhiều hội chứng rối loạn như Guillain-Barre ở người lớn, song chưa c&oacute; vắc xin hay thuốc đặc trị.</span></span></span></p>\r\n\r\n			<p style="text-align:justify"><span style="font-size:10pt"><span style="font-family:arial"><span style="color:rgb(17, 17, 17)">Theo WHO, hiện c&oacute; hơn 60 c&ocirc;ng ty v&agrave; c&aacute;c viện nghi&ecirc;n cứu tham gia ph&aacute;t triển vắc xin ph&ograve;ng chống Zika, trong đ&oacute; c&oacute; 18 mẫu vắcxin d&agrave;nh ri&ecirc;ng cho phụ nữ trong độ tuổi sinh sản.</span></span></span></p>\r\n\r\n			<div>&nbsp;</div>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '5', 0, '2017-04-12 16:00:26', '0000-00-00 00:00:00', 'my-thu-thanh-cong-vac-xin-phong-chong-virus-zika-tren-dong-vat', 'post'),
(39, 7, 'Israel thử nghiệm thành công insulin dạng viên', '<p><img src="http://curminlead.ecpvn.com/frontend/web/uploads/images/2017/04/hr3YT29-1492012746.jpg" /></p>\r\n\r\n<p>Cho đến nay, việc ti&ecirc;m insulin v&agrave;o cơ thể được cho l&agrave; g&acirc;y nhiều đau đớn v&agrave; c&oacute; nguy cơ g&acirc;y nhiễm tr&ugrave;ng. C&aacute;c nh&agrave; khoa học của nhiều nước tr&ecirc;n thế giới đ&atilde; t&iacute;ch cực nghi&ecirc;n cứu vấn đề n&agrave;y nhưng chưa đạt được kết quả t&iacute;ch cực</p>\r\n\r\n<p>Do vậy, nếu kết quả của C&ocirc;ng ty Oramed Pharmaceuticals được thẩm định v&agrave; c&ocirc;ng nhận, insulin dạng vi&ecirc;n được d&ugrave;ng thay thế cho insulin dạng ti&ecirc;m. Điều n&agrave;y kh&ocirc;ng chỉ gi&uacute;p l&agrave;m giảm đau đớn cho bệnh nh&acirc;n m&agrave; c&ograve;n tạo thuận lợi cho c&aacute;c b&aacute;c sĩ trong việc điều trị căn bệnh đ&aacute;i th&aacute;o đường typ 2.</p>\r\n\r\n<p>Nhiều người cho rằng việc d&ugrave;ng insulin bằng đường uống c&oacute; thể bị ph&aacute; hủy bởi dịch ti&ecirc;u h&oacute;a. Tuy nhi&ecirc;n, kết quả thử nghiệm của C&ocirc;ng ty Ormed Pharmaceuticals cho thấy, insulin dạng vi&ecirc;n c&oacute; thể l&agrave;m giảm lượng đường trong m&aacute;u v&agrave;o ban đ&ecirc;m cho bệnh nh&acirc;n đ&aacute;i th&aacute;o đường typ 2.</p>\r\n', '5', 0, '2017-04-12 16:01:13', '0000-00-00 00:00:00', 'israel-thu-nghiem-thanh-cong-insulin-dang-vien', 'post'),
(40, 7, 'TINH NGHỆ NOVASOL CURCUMIN DƯỚI ĐÁNH GIÁ CHUYÊN GIA', '<div class="des-news" style="color: rgb(68, 68, 68); float: left; font-weight: 700; font-stretch: normal; font-size: 14px; line-height: 18px; font-family: arial; padding-bottom: 10px; text-rendering: geometricPrecision; width: 715px;">Đau dạ d&agrave;y hay c&aacute;c bệnh về ti&ecirc;u h&oacute;a l&agrave; những căn bệnh dai dẳng, kh&oacute; điều trị dứt điểm tr&ecirc;n l&acirc;m s&agrave;ng nếu chỉ sử dụng kh&aacute;ng sinh. Gần đ&acirc;y, việc sử dụng nghệ v&agrave;ng trong hỗ trợ điều trị bệnh dạ d&agrave;y, ti&ecirc;u h&oacute;a đ&atilde; trở n&ecirc;n phổ biến. Đặc biệt việc d&ugrave;ng chế phẩm chiết xuất theo c&ocirc;ng nghệ m&agrave;ng sinh hoc nano &ndash; Novasol Curcumin đ&atilde; đạt được những hiệu quả vượt trội tr&ecirc;n l&acirc;m s&agrave;ng.</div>\r\n\r\n<div class="Other" style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 12px;">&nbsp;</div>\r\n\r\n<div class="contents" style="float: left; font-stretch: normal; font-size: 14px; line-height: 22px; font-family: arial; padding-top: 10px; width: 700.688px;">Trong Hội nghị ti&ecirc;u h&oacute;a th&agrave;nh phố H&agrave; Nội với chủ đề &ldquo;Vai tr&ograve; của thực phẩm chức năng trong hỗ trợ điều trị c&aacute;c bệnh dạ d&agrave;y, ti&ecirc;u h&oacute;a&rdquo; c&oacute; sự tham gia của PGS.TS Phạm Thị Thu Hồ - Nguy&ecirc;n PCT Hội ti&ecirc;u h&oacute;a Việt Nam, Nguy&ecirc;n trưởng khoa ti&ecirc;u h&oacute;a BV Bạch Mai, PGS. TS Nguyễn Thượng Dong &ndash; Nguy&ecirc;n viện trưởng Viện Dược liệu &ndash; Bộ Y tế &nbsp;v&agrave; gần 200 b&aacute;c sĩ th&agrave;nh vi&ecirc;n Hội Ti&ecirc;u h&oacute;a H&agrave; Nội.&nbsp;</div>\r\n\r\n<div class="contents" style="float: left; font-stretch: normal; font-size: 14px; line-height: 22px; font-family: arial; padding-top: 10px; width: 700.688px;">&nbsp;</div>\r\n\r\n<p><img src="http://curminlead.ecpvn.com/frontend/web/uploads/images/2017/04/pVkBwyF-1492020728.jpg" style="height:467px; width:700px" /></p>\r\n\r\n<div style="font-family: arial; font-size: 14px;">\r\n<div style="text-align: -webkit-center;"><em>PGS. TS Phạm Thị Thu Hồ chủ tr&igrave; Hội nghị&nbsp;</em></div>\r\n\r\n<div>Thực tế cho thấy bệnh dạ d&agrave;y v&agrave; ti&ecirc;u h&oacute;a ng&agrave;y c&agrave;ng tăng mạnh, trở th&agrave;nh căn bệnh phổ biến trong x&atilde; hội hiện đại ng&agrave;y nay, nguy&ecirc;n nh&acirc;n h&agrave;ng đầu l&agrave; do lối sống c&ocirc;ng nghiệp ảnh hưởng đến sinh hoạt ăn uống. Khi đ&atilde; mắc bệnh về ti&ecirc;u h&oacute;a th&igrave; việc điều trị c&agrave;ng kh&oacute; khăn hơn, kh&aacute;ng sinh một phần kh&oacute; đ&aacute;p ứng, người bệnh cần thay đổi lối sống trong kết hợp điều trị. Thậm ch&iacute; c&aacute;c nghi&ecirc;n cứu c&ograve;n cho thấy nếu li&ecirc;n tục sử dụng thuốc giảm đau hay kh&aacute;ng sinh tr&ecirc;n một năm c&oacute; thể g&acirc;y ra t&igrave;nh trạng xuất huyết dạ d&agrave;y. Hội nghị ti&ecirc;u h&oacute;a đ&atilde; đưa ra c&acirc;u hỏi: đ&aacute;p ứng c&aacute;c kh&oacute; khăn trong điều trị đ&oacute; th&igrave; việc sử dụng nghệ hỗ trợ điều trị c&oacute; khả thi hay kh&ocirc;ng?&nbsp;</div>\r\n</div>\r\n\r\n<div style="font-family: arial; font-size: 14px;">Trả lời vấn đề n&agrave;y, PGS.TS Phạm Thị Thu Hồ cho biết: &ldquo;Trong nghệ c&oacute; chất curcumin được chứng minh chống ung thư v&agrave; điều trị vi&ecirc;m lo&eacute;t dạ d&agrave;y t&aacute; tr&agrave;ng. N&ecirc;n nghệ được c&ocirc;ng nhận khả năng l&agrave;m l&agrave;nh vết thương, phục hồi c&aacute;c tế b&agrave;o bị tổn thương nhanh ch&oacute;ng v&agrave; hỗ trợ cơ thể tự sản sinh miễn dịch hạn chế bệnh t&aacute;i ph&aacute;t. Thế nhưng chất curcumin n&agrave;y chỉ chiếm 2-5% v&agrave; c&oacute; nhược điểm l&agrave; kh&ocirc;ng tan trong nước v&igrave; vậy &iacute;t được hấp thu ở trong ruột v&agrave; đưa v&agrave;o m&aacute;u, phần lớn sẽ bị đ&agrave;o thải ra ngo&agrave;i. Ch&iacute;nh v&igrave; thế m&agrave; tinh bột nghệ kh&ocirc;ng ph&aacute;t huy được hết t&aacute;c dụng điều trị. Khắc phục những vấn đề đ&oacute; y học hiện đại đ&atilde; ph&aacute;t minh ra những chế phẩm th&ocirc;ng minh từ nghệ, gần đ&acirc;y nhất l&agrave; c&ocirc;ng nghệ m&agrave;ng sinh học nano &ndash; Nano Curcumin thế hệ mới.&rdquo;</div>\r\n\r\n<div style="font-family: arial; font-size: 14px;">\r\n<div style="font-family: arial; font-size: 14px;">Nhắc đến c&ocirc;ng nghệ m&agrave;ng sinh học nano &ndash; Nano curcumin thế hệ mới &ndash; cũng từng g&acirc;y nhiều thắc mắc gần đ&acirc;y, người ta băn khoăn liệu khả năng hấp thu v&agrave; đ&aacute;p ứng việc điều trị c&oacute; được như mong đợi. Giải đ&aacute;p vấn đề n&agrave;y, PGS.TS Nguyễn Thượng Dong cho biết tại hội nghị: &ldquo;Cũng như PGS. TS Phạm Thị Thu Hồ đ&atilde; n&oacute;i ở tr&ecirc;n, nghệ kh&ocirc;ng tan trong nước v&agrave; dung m&ocirc;i dầu. Nano curcumin thế hệ mới (Novasol Curcumin) ra đời khắc phục hạn chế đ&oacute; của Curcumin được c&aacute;c nh&agrave; khoa học Đức ph&aacute;t minh. Nghi&ecirc;n cứu từng được cấp bằng s&aacute;ng chế bởi tổ chức sở hữu tr&iacute; tuệ thế giới WIPO ng&agrave;y 26/06/2014. Novasol Curcumin được ph&acirc;n t&aacute;ch từ c&aacute;c ph&acirc;n tử Curcumin c&oacute; k&iacute;ch thước si&ecirc;u nhỏ, đồng bộ từ 4-40nm, ưu ti&ecirc;n k&iacute;ch thước 30nm, gi&uacute;p tăng tốc độ hấp thu v&agrave; khả năng hấp thu, sinh khả dụng tăng 185 lần so với curcumin th&ocirc;ng thường. Đặc biệt Novasol Curcumin ổn định trong mọi m&ocirc;i trường pH dạ d&agrave;y v&agrave; ruột. Chế phẩm Novasol Curcumin được đ&aacute;nh gi&aacute; l&agrave; ưu việt hơn hẳn trong hỗ trợ điều trị bệnh dạ d&agrave;y, ti&ecirc;u h&oacute;a. Thực tế, bệnh nh&acirc;n c&oacute; thể thấy r&otilde; rệt c&aacute;c triệu chứng đau dạ d&agrave;y giảm ho&agrave;n to&agrave;n sau 2 tuần sử dụng.&rdquo;</div>\r\n\r\n<div style="font-family: arial; font-size: 14px;">&nbsp;</div>\r\n\r\n<div style="font-family: arial; font-size: 14px;">Curmin Lead - chế phẩm chứa Novasol curcumin duy nhất tại Việt Nam hiện nay được nhắc đến như một sản phẩm an to&agrave;n, kh&ocirc;ng c&oacute; t&aacute;c dụng phụ v&agrave; hiệu quả trong hỗ trợ điều trị vi&ecirc;m lo&eacute;t dạ d&agrave;y, h&agrave;nh t&aacute; tr&agrave;ng, vi&ecirc;m đại tr&agrave;ng, vi&ecirc;m ruột&hellip; th&ecirc;m lựa chọn mới cho bệnh nh&acirc;n dạ d&agrave;y-ung bướu</div>\r\n\r\n<div style="font-family: arial; font-size: 14px;">\r\n<div style="font-family: arial; font-size: 14px;">Tại Việt Nam, lần đầu ti&ecirc;n Novasol Curcumin được b&agrave;o chế ứng dụng trong sản phẩm CURMIN LEAD - Th&ecirc;m lựa chọn mới cho bệnh nh&acirc;n dạ d&agrave;y-ung bướu.</div>\r\n\r\n<div style="font-family: arial; font-size: 14px;">CURMIN LEAD gi&uacute;p :</div>\r\n\r\n<div style="font-family: arial; font-size: 14px;">&bull; Hỗ trợ điều trị vi&ecirc;m lo&eacute;t dạ d&agrave;y, h&agrave;nh t&aacute; tr&agrave;ng, vi&ecirc;m đại tr&agrave;ng, vi&ecirc;m ruột</div>\r\n\r\n<div style="font-family: arial; font-size: 14px;">&bull; L&agrave;m giảm t&aacute;c dụng phụ của h&oacute;a trị, xạ trị như: mệt mỏi, mất ngủ, trầm cảm, đau d&acirc;y thần kinh.</div>\r\n\r\n<div style="font-family: arial; font-size: 14px;">&bull; Ngăn ngừa bệnh ung bướu,chống l&atilde;o h&oacute;a, nhanh liền sẹo,tốt cho gan mật,xương khớp.</div>\r\n\r\n<div style="font-family: arial; font-size: 14px;">&bull; Phục hồi sức khỏe sắc đẹp cho phụ nữ sau sinh.</div>\r\n\r\n<div style="font-family: arial; font-size: 14px;">&nbsp;</div>\r\n\r\n<div style="font-family: arial; font-size: 14px;">&nbsp;Tiếp thị v&agrave; ph&acirc;n phối: NAFACO JSC</div>\r\n\r\n<div style="font-family: arial; font-size: 14px;">&nbsp;</div>\r\n</div>\r\n</div>\r\n\r\n<p><img src="http://curminlead.ecpvn.com/frontend/web/uploads/images/2017/04/sADrowK-1492020803.jpg" style="height:231px; width:700px" /></p>\r\n\r\n<div style="font-family: arial; font-size: 14px;">Địa chỉ: &nbsp;119 Nguyễn Ngọc Nại, Thanh Xu&acirc;n, &nbsp;H&agrave; Nội Số: 1902/2014/ XNQC-ATTP Số giấy ph&eacute;p quảng c&aacute;o: 238/2015/XNQC-ATTP</div>\r\n\r\n<div style="font-family: arial; font-size: 14px;">SĐT tư vấn: 04 6251 2999 hoặc 08 3850 6989</div>\r\n\r\n<div style="font-family: arial; font-size: 14px;">Website: &nbsp;www.curminlead.com</div>\r\n\r\n<div style="font-family: arial; font-size: 14px;">Sản phẩm n&agrave;y kh&ocirc;ng phải l&agrave; thuốc, kh&ocirc;ng d&ugrave;ng thay thế thuốc chữa bệnh&nbsp;</div>\r\n\r\n<div style="font-family: arial; font-size: 14px;"><strong>Để tri &acirc;n kh&aacute;ch h&agrave;ng, từ ng&agrave;y 03/11/2016 nh&atilde;n h&agrave;ng Curmin Lead thực hiện chương tr&igrave;nh:</strong></div>\r\n\r\n<div style="font-family: arial; font-size: 14px;"><strong>&quot;Trao gửi y&ecirc;u thương - Ki&ecirc;n cường chiến đấu&quot;</strong></div>\r\n\r\n<div style="font-family: arial; font-size: 14px;"><strong>&nbsp;Hỗ trợ gi&aacute; cho bệnh nh&acirc;n ung thư 180.000d/ hộp (Gi&aacute; th&ocirc;ng thường 240.000đ/hộp)</strong></div>\r\n', '5', 0, '2017-04-12 18:13:54', '0000-00-00 00:00:00', 'tinh-nghe-novasol-curcumin-duoi-danh-gia-chuyen-gia', 'post'),
(42, 7, 'Ung thư dạ dày rất dễ nhầm lẫn với bệnh viêm dạ dày thông thường', '<div class="des-news" style="color: rgb(68, 68, 68); float: left; font-weight: 700; font-stretch: normal; font-size: 14px; line-height: 18px; font-family: arial; padding-bottom: 10px; text-rendering: geometricPrecision; width: 715px;">Việt Nam l&agrave; một nước c&oacute; tỷ lệ mắc bệnh về dạ d&agrave;y cao trong khu vực v&agrave; tr&ecirc;n thế giới. Từ vi&ecirc;m lo&eacute;t dạ d&agrave;y nếu kh&ocirc;ng được điều trị kịp thời c&oacute; thể dẫn đến c&aacute;c bệnh l&yacute; nguy hiểm hơn như: lo&eacute;t dạ d&agrave;y, chảy m&aacute;u dạ d&agrave;y v&agrave; c&oacute; thể dẫn đến ung thư dạ d&agrave;y. Ở nước ta mỗi năm c&oacute; khoảng 15.000 trường hợp được chuẩn đo&aacute;n ung thư dạ d&agrave;y v&agrave; 11.000 trường hợp tử vong. Ung thư dạ d&agrave;y l&agrave; loại ung thư nguy hiểm thứ 2 ở nam giới sau ung thư phổi, thứ 3 ở nữ giớisau ung thư v&uacute; v&agrave; cổ tử cung.</div>\r\n\r\n<div class="Other" style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 12px;">&nbsp;</div>\r\n\r\n<div class="contents" style="float: left; font-stretch: normal; font-size: 14px; line-height: 22px; font-family: arial; padding-top: 10px; width: 700.688px;">D&ugrave; nguy hiểm nhưng nếu như bệnh ung thư dạ d&agrave;y được ph&aacute;t hiện ở giai đoạn sớm, kết hợp với phương ph&aacute;p phẫu thuật v&agrave; điều trị hợp l&yacute; th&igrave; tỷ lệ chiến thắng bệnh v&agrave;o khoảng 99%. &nbsp;Mặc d&ugrave; vậy c&oacute; một thực tế kh&ocirc;ng phải ai cũng biết đ&oacute; l&agrave; tỷ lệ ph&aacute;t hiện ung thư dạ d&agrave;y sớm ở Việt Nam rất thấp, đa phần bệnh được ph&aacute;t hiện khi đ&atilde; ở giai đoạn cuối, tỷ lệ n&agrave;y chiếm khoảng 75%.</div>\r\n\r\n<div class="contents" style="float: left; font-stretch: normal; font-size: 14px; line-height: 22px; font-family: arial; padding-top: 10px; width: 700.688px;">&nbsp;</div>\r\n\r\n<p><img src="http://curminlead.ecpvn.com/frontend/web/uploads/images/2017/04/xfokAxZ-1492022174.jpg" /></p>\r\n\r\n<p><strong>Nguy&ecirc;n nh&acirc;n dẫn đến nhầm lẫn</strong></p>\r\n\r\n<div style="font-family: arial; font-size: 14px;">Nguy&ecirc;n nh&acirc;n c&oacute; thể kể đến yếu tố chủ quan. Đa phần mọi người, khi cơ thể c&oacute; c&aacute;c triệu chứng như đau bụng, ăn kh&ocirc;ng ngon, đầy hơi ợ chua... đều chỉ nghĩ l&agrave; đau bụng th&ocirc;ng thường v&agrave; phương ph&aacute;p cấp b&aacute;ch l&agrave; mua thuốc giảm đau để uống. Với việc uống thuốc kh&ocirc;ng đ&uacute;ng c&aacute;ch, kết hợp với t&acirc;m l&yacute; chủ quan trong ăn uống, sinh hoạt&hellip;do kh&ocirc;ng x&aacute;c định đ&uacute;ng t&igrave;nh trạng bệnh khiến cho bệnh t&igrave;nh trở n&ecirc;n trầm trọng hơn, bệnh ph&aacute;t triển nhanh hơn. Triệu chứng của ung thư dạ d&agrave;y rất dễ nhầm lần với bệnh đau dạ d&agrave;y th&ocirc;ng thường.Khi bị ung thư dạ d&agrave;y ở giai đoạn đầu, thường c&oacute; c&aacute;c triệu chứng kh&oacute; ti&ecirc;u, n&oacute;ng r&aacute;t v&ugrave;ng thượng vị, ăn kh&ocirc;ng ngon miệng&hellip; Đến giai đoạn nặng hơn, người bệnh cảm thấy mệt mỏi, đầy bụng sau khi ăn. Khi bệnh ph&aacute;t triển ở giai đoạn cuối mới biểu hiện triệu chứng đau bụng, n&ocirc;n v&agrave; buồn n&ocirc;n, loạn ti&ecirc;u h&oacute;a, s&uacute;t c&acirc;n, xuất huyết ti&ecirc;u h&oacute;a.</div>\r\n\r\n<div style="font-family: arial; font-size: 14px;">&nbsp;</div>\r\n\r\n<div style="font-family: arial; font-size: 14px;">Ngo&agrave;i ra đa số người Việt Nam kh&ocirc;ng c&oacute; th&oacute;i quen kh&aacute;m bệnh định k&igrave;, chỉ khi cơ thể c&oacute; dấu hiệu bất thường, biểu hiện của bệnh kh&aacute; r&otilde; th&igrave; mới t&igrave;m đến b&aacute;c sĩ v&agrave; bệnh viện.</div>\r\n\r\n<div style="font-family: arial; font-size: 14px;">&nbsp;</div>\r\n\r\n<div style="font-family: arial; font-size: 14px;">Nội soi dạ d&agrave;y được coi l&agrave; phương ph&aacute;p chuẩn đo&aacute;n c&aacute;c bệnh li&ecirc;n quan đến dạ d&agrave;y. Tuy nhi&ecirc;n, nhược điểm của phương ph&aacute;p n&agrave;y l&agrave; đau v&agrave; l&agrave;m người bệnh bị &aacute;m ảnh, nhiều người đ&atilde; n&ocirc;n mửa nặng khi đưa ống nội soi v&agrave;o cổ họng. Ch&iacute;nh v&igrave; vậy, người bệnh c&oacute; t&acirc;m l&yacute; ngại đi kh&aacute;m v&agrave; thường v&ocirc; t&igrave;nh bỏ mặc sức khỏe của ch&iacute;nh bản th&acirc;n m&igrave;nh.</div>\r\n\r\n<div style="font-family: arial; font-size: 14px;">&nbsp;</div>\r\n\r\n<div style="font-family: arial; font-size: 14px;">&nbsp;</div>\r\n\r\n<div style="font-family: arial; font-size: 14px;"><strong>Ph&ograve;ng bệnh hơn chữa b&ecirc;nh</strong></div>\r\n\r\n<div style="font-family: arial; font-size: 14px;">Đối với rất nhiều người, họ kh&ocirc;ng &yacute; thức việc ph&ograve;ng bệnh, chỉ đến khi mắc bệnh, bản th&acirc;n phải trải qua đau đớn của b&ecirc;nh tật mới thấm th&iacute;a được sức khỏe qu&yacute; gi&aacute; như thế n&agrave;o. Đ&atilde; kh&ocirc;ng &iacute;t người khi đứng trước c&aacute;nh cửa tử thần vẫn hay n&oacute;i c&acirc;u &ldquo; gi&aacute; như&rdquo; th&igrave; đ&atilde; muộn. Đối với căn bệnh dạ d&agrave;y hay ung thư dạ d&agrave;y cũng như v&ocirc; v&agrave;n bệnh kh&aacute;c nếu tr&aacute;nh được l&agrave; điều may mắn nhất. Một khi đ&atilde; mắc bệnh, đặc biệt l&agrave; ung thư th&igrave; kh&ocirc;ng những tỷ lệ sống v&ocirc; c&ugrave;ng thấp m&agrave; bản th&acirc;n người bệnh cũng phải trải qua v&ocirc; v&agrave;n đau đớn cả về thể x&aacute;c lẫn tinh thần. C&acirc;u n&oacute;i &ldquo; ph&ograve;ng bệnh hơn chữa b&ecirc;nh&rdquo; &nbsp;rất đ&aacute;ng để ch&uacute;ng ta suy ngẫm.</div>\r\n\r\n<div style="font-family: arial; font-size: 14px;">&nbsp;</div>\r\n\r\n<div style="font-family: arial; font-size: 14px;">Curcumin, tinh chất qu&yacute; từ nghệ tươi được xem l&agrave; thảo dược tốt cho ti&ecirc;u h&oacute;a v&agrave; c&oacute; t&aacute;c dụng chống ung thư. Với những nghi&ecirc;n cứu gần đ&acirc;y, Curcumin mở ra cho con người triển vọng giảm thiểu nguy cơ ung thư v&agrave; gi&uacute;p bảo vệ sức khỏe con người.</div>\r\n', '5', 0, '2017-04-12 18:36:28', '0000-00-00 00:00:00', 'ung-thu-da-day-rat-de-nham-lan-voi-benh-viem-da-day-thong-thuong', 'post'),
(43, 7, 'Bệnh ung thư vòm họng', '<div class="des-news" style="color: rgb(68, 68, 68); float: left; font-weight: 700; font-stretch: normal; font-size: 14px; line-height: 18px; font-family: arial; padding-bottom: 10px; text-rendering: geometricPrecision; width: 715px;">Bệnh nh&acirc;n c&oacute; một tổ chức lo&eacute;t, s&ugrave;i &aacute;c t&iacute;nh ở bất cứ vị tr&iacute; n&agrave;o trong v&ograve;m họng. Nguy&ecirc;n nh&acirc;n ch&iacute;nh g&acirc;y ung thư v&ograve;m họng chưa được x&aacute;c định, nhưng nhiều nghi&ecirc;n cứu cho thấy bệnh c&oacute; li&ecirc;n quan tới virus EBV. C&aacute;c yếu tố nguy cơ kh&aacute;c của bệnh l&agrave; vi&ecirc;m họng mạn t&iacute;nh k&eacute;o d&agrave;i, nghiện thuốc l&aacute;, uống rượu nhiều.</div>\r\n\r\n<div class="Other" style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 12px;">&nbsp;</div>\r\n\r\n<div class="contents" style="float: left; font-stretch: normal; font-size: 14px; line-height: 22px; font-family: arial; padding-top: 10px; width: 700.688px;">\r\n<div>Ở giai đoạn sớm c&oacute; tới 80% bệnh nh&acirc;n ung thư v&ograve;m họng bị nhức đầu lan tỏa, &acirc;m ỉ v&agrave; thường ở một b&ecirc;n đầu. &Ugrave; tai chiếm 70% ca bệnh, đa số &ugrave; một b&ecirc;n tai, l&uacute;c n&agrave;o cũng như c&oacute; tiếng ve k&ecirc;u, ngạt một b&ecirc;n mũi l&uacute;c nặng, l&uacute;c nhẹ, c&oacute; khi chảy m&aacute;u mũi. Ở giai đoạn muộn, người bệnh nhức đầu li&ecirc;n tục, c&oacute; l&uacute;c đau dữ dội. &Ugrave; tai tăng dần dẫn tới nghe k&eacute;m hoặc điếc. Mũi ngạt nặng li&ecirc;n tục v&agrave; thường c&oacute; chảy m&aacute;u mũi.</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>Tổ chức ung thư v&ograve;m họng khi x&acirc;m lấn tới nền sọ (giai đoạn muộn) c&oacute; thể g&acirc;y tổn thương c&aacute;c d&acirc;y thần kinh sọ n&atilde;o. Thường 80% số bệnh nh&acirc;n khi được chẩn đo&aacute;n ung thư v&ograve;m họng đ&atilde; c&oacute; hạch cổ. Hạch thường nằm ở c&ugrave;ng b&ecirc;n với khối u. Trong số đ&oacute; c&oacute; tr&ecirc;n 60% bệnh nh&acirc;n nổi hạch cổ ngay từ đầu v&agrave; ch&iacute;nh v&igrave; sự nổi hạch bất thường n&agrave;y m&agrave; người bệnh đi kh&aacute;m.</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>Khi bị ung thư v&ograve;m họng, c&aacute;c x&eacute;t nghiệm cần l&agrave;m l&agrave; chẩn đo&aacute;n tế b&agrave;o học v&agrave; m&ocirc; bệnh học. Chẩn đo&aacute;n tế b&agrave;o học l&agrave; lấy tế b&agrave;o bong ở khối u bằng tăm b&ocirc;ng, hoặc chọc kim nhỏ v&agrave;o hạch lấy &iacute;t tế b&agrave;o, cố định tr&ecirc;n lam k&iacute;nh, nhuộm rồi soi tr&ecirc;n k&iacute;nh hiển vi quang học c&oacute; độ ph&oacute;ng đại 100 đến 1.000 lần. Chẩn đo&aacute;n m&ocirc; bệnh học l&agrave; lấy một mảnh nhỏ ở tổ chức u hoặc nghi ngờ khối u, hoặc lấy ở hạch cổ bằng kim bấm chuy&ecirc;n dụng. Mảng sinh thiết được cố định bằng dung dịch chuy&ecirc;n dụng, sau đ&oacute; v&ugrave;i trong paraphin rồi cắt mỏng 3-4 phần ngh&igrave;n milim&eacute;t, nhuộm v&agrave; soi dưới k&iacute;nh hiển vi quang học.</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>Ngo&agrave;i ra, c&oacute; thể l&agrave;m c&aacute;c x&eacute;t nghiệm kh&aacute;c như: chụp X-quang hộp sọ, chụp cắt lớp vi t&iacute;nh, chụp X-quang phổi, si&ecirc;u &acirc;m gan, x&eacute;t nghiệm miễn dịch về virus EBV.</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>Ung thư v&ograve;m họng được điều trị chủ yếu bằng tia xạ. C&aacute;c trường hợp muộn c&oacute; kết hợp h&oacute;a trị. Để điều trị di căn tới hạch, xương, phổi, gan..., t&ugrave;y trường hợp, c&oacute; thể d&ugrave;ng phương ph&aacute;p cắt bỏ kết hợp với tia xạ hoặc h&oacute;a chất.</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>BS Nguyễn Phương, Sức Khỏe &amp; Đời Sống</div>\r\n</div>\r\n', '5', 0, '2017-04-12 18:37:01', '0000-00-00 00:00:00', 'benh-ung-thu-vom-hong', 'post'),
